import { 
  createWorkflow
} from "@medusajs/workflows-sdk"
import {
  useRemoteQueryStep
} from "@medusajs/core-flows"

const helloWorldWorkflow = createWorkflow(
  "hello-world",
  () => {
    const product = useRemoteQueryStep({
      entry_point: "product",
      fields: [
        "*",
        "variants.*"
      ],
      variables: {
        filters: {
          id: "123"
        }
      },
      list: false
    })
  }
)
